import "./style/App.css";

function App() {
  const [time, setTime] = useState("TIME");

  function GetTime() {
    let updateTime = new Date().toLocaleTimeString();
    setTime(updateTime);
  }

  return (
    <div className="container">
      <h1>{time}</h1>
      <button onClick={GetTime}>Get Time</button>
    </div>
  );
}

export default App;
